-- 1. Create the fmr_projects table (does NOT alter existing projects table)
CREATE TABLE IF NOT EXISTS public.fmr_projects (
  id                    BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  year_funded           INTEGER,
  project_name          TEXT NOT NULL,
  location              TEXT DEFAULT '',
  municipality          TEXT DEFAULT '',
  province              TEXT DEFAULT 'Iloilo',
  region                TEXT DEFAULT 'Region VI - Western Visayas',
  target_completion_date TEXT DEFAULT '',
  date_completed        TEXT DEFAULT '',
  status                TEXT NOT NULL DEFAULT 'Proposed',
  remarks               TEXT DEFAULT '',
  accomplishment        INTEGER DEFAULT 0,
  project_length_km     DOUBLE PRECISION DEFAULT 0,
  start_latitude        DOUBLE PRECISION,
  start_longitude       DOUBLE PRECISION,
  end_latitude          DOUBLE PRECISION,
  end_longitude         DOUBLE PRECISION,
  source                TEXT DEFAULT 'DA-RAED Region VI',
  created_at            TIMESTAMPTZ DEFAULT now(),
  updated_at            TIMESTAMPTZ DEFAULT now()
);

-- 2. Enable Row Level Security
ALTER TABLE public.fmr_projects ENABLE ROW LEVEL SECURITY;

-- 3. RLS Policies
CREATE POLICY "Anyone authenticated can view fmr_projects"
  ON public.fmr_projects FOR SELECT TO authenticated USING (true);

CREATE POLICY "Anon users can view fmr_projects"
  ON public.fmr_projects FOR SELECT TO anon USING (true);

CREATE POLICY "Authenticated users can insert fmr_projects"
  ON public.fmr_projects FOR INSERT TO authenticated WITH CHECK (true);

CREATE POLICY "Authenticated users can update fmr_projects"
  ON public.fmr_projects FOR UPDATE TO authenticated USING (true);

CREATE POLICY "Authenticated users can delete fmr_projects"
  ON public.fmr_projects FOR DELETE TO authenticated USING (true);

-- 4. Enable Realtime
DO $$
BEGIN
  IF NOT EXISTS (
    SELECT 1 FROM pg_publication_tables
    WHERE pubname = 'supabase_realtime' AND tablename = 'fmr_projects'
  ) THEN
    ALTER PUBLICATION supabase_realtime ADD TABLE public.fmr_projects;
  END IF;
END $$;

